class TextCleaner:
    def __init__(self):
        self.word_index_dictionary = dict

    def __call__(self, text):
        indexes = []
        for char in text:
            try:
                indexes.append(self.word_index_dictionary[char])
            except KeyError:
                print(char)
        return indexes
